```javascript
//定义视频流展示区域和控制区域变量
var video_stream = document.getElementById('video-stream');
var system_operation = document.getElementById('system-operation');

//创建WebRTC Peer Connection对象
var peer = new RTCPeerConnection();

//添加本地MediaStream
navigator.mediaDevices.getUserMedia({video: true, audio: true})
.then(function(stream) {
  localStream = stream;
  peer.addStream(localStream);
  video_stream.srcObject = stream;
})
.catch(function(error) {
  console.log('getUserMedia error: ' + error);
});

//创建Socket.IO连接对象
var socket = io();

//当socket连接成功后，将本地SDP信息通过socket发送给服务器
socket.on('connect', function() {
  console.log('Connected to server');
  peer.createOffer(function(offer) {
    peer.setLocalDescription(offer);
    socket.emit('offer', offer);
  }, function(error) {
    console.log('Create offer error: ' + error);
  });
});

//当收到服务器转发的远端SDP信息时，将其设置到peer对象中
socket.on('answer', function(answer) {
  console.log('Receive answer: ' + answer.sdp);
  peer.setRemoteDescription(new RTCSessionDescription(answer));
});

//当收到服务器转发的ICE Candidate信息时，将其设置到peer对象中
socket.on('candidate', function(candidate) {
  console.log('Receive candidate: ' + candidate.candidate);
  peer.addIceCandidate(new RTCIceCandidate(candidate));
});

//当检测到peer对象中的ICE Candidate信息发生变化时，将其发送给服务器
peer.onicecandidate = function(event) {
  if (event.candidate) {
    socket.emit('candidate', event.candidate);
  }
};

//当检测到peer对象中的MediaStream发生变化时，将其更新到页面上的视频流展示区域
peer.onaddstream = function(remoteStream) {
  console.log('Remote stream added');
  video_stream.srcObject = remoteStream.stream;
};

//定义系统操作区域的HTML元素和事件处理程序
var start_button = '<button type="button" onclick="start()">Start</button>';
var stop_button = '<button type="button" onclick="stop()">Stop</button>';
system_operation.innerHTML = start_button;

function start() {
  localStream.getTracks().forEach(track => track.enabled = true);
  system_operation.innerHTML = stop_button;
}

function stop() {
  localStream.getTracks().forEach(track => track.enabled = false);
  system_operation.innerHTML = start_button;
}
```

