```javascript
class RandomNumberGenerator {
  // 生成[a, b]范围内的随机整数
  static randomInt(a, b) {
    return Math.floor(Math.random() * (b - a + 1)) + a;
  }

  // 生成[a, b]范围内的随机浮点数
  static randomFloat(a, b) {
    return Math.random() * (b - a) + a;
  }

  // 按照权重生成随机数
  static randomWeighted(weights, values) {
    const totalWeight = weights.reduce((a, b) => a + b, 0);
    const randomValue = Math.random() * totalWeight;
    let accumulatedWeight = 0;

    for (let i = 0; i < weights.length; i++) {
      accumulatedWeight += weights[i];
      if (randomValue <= accumulatedWeight) {
        return values[i];
      }
    }
  }
}

const form = document.getElementById("randomForm");
const result = document.getElementById("result");

form.addEventListener("submit", event => {
  event.preventDefault();

  const min = Number(form.min.value);
  const max = Number(form.max.value);
  const type = form.type.value;
  const weights = form.weights.value.split(",").map(Number);

  if (weights.length > 1) {
    const values = type === "integer" ? range(min, max) : range(min, max, false);
    const randomNumber = RandomNumberGenerator.randomWeighted(weights, values);
    result.innerHTML = randomNumber.toFixed(2);
  } else {
    let randomNumber;
    if (type === "integer") {
      randomNumber = RandomNumberGenerator.randomInt(min, max);
    } else {
      randomNumber = RandomNumberGenerator.randomFloat(min, max);
    }

    result.innerHTML = randomNumber.toFixed(2);
  }
});

function range(min, max, isInteger = true) {
  const result = [];
  const step = isInteger ? 1 : 0.1;

  for (let i = min; i <= max; i += step) {
    result.push(parseFloat(i.toFixed(2)));
  }
  return result;
}
