        for j in range(len(grid)):
            if grid[i][j] >= 0:
                print(grid[i][j], end=" ")
            else:
                print("*", end=" ")
        print()

# 点开空方块函数
def openEmpty(i, j, grid, visited):
    if (i, j) in visited:
        return
    visited.append((i, j))
    grid[i][j] = -2
    if i-1 >= 0 and j-1 >= 0 and grid[i-1][j-1] == 0:
        openEmpty(i-1, j-1, grid, visited)
    if i-1 >= 0 and grid[i-1][j] == 0:
